exports.multiply = function(a, b){
    return +a * +b;
};

exports.divide = function(a, b){
    if(!b)
    {
        throw new Error('Operand B must be different of zero.');
    }
    
    return +a / +b;
};

exports.sum = function(a, b){
    return +a + +b;
};

exports.subtract = function(a, b){
    return +a - +b;
};