var calculator = require('../calculator');

describe('Calculator', function(){
   describe('multiplication', function(){
       it('should multiply 3 and 9', function(){
           var multiplication = calculator.multiply(3, 9);
           expect(multiplication).toBe(27);
       });
       
       it('should multiply 5 and 0.5', function(){
           var multiplication = calculator.multiply(5, 0.5);
           expect(multiplication).toBe(2.5);
       });
   });
   
   describe('sum', function(){
       it('should sum 6 and 13', function(){
           var sum = calculator.sum(6, 13);
           expect(sum).toBe(19);
       });
   });
   
   describe('subtraction', function(){
       it('should subtract 7 and 12', function(){
           var subtraction = calculator.subtract(7, 12);
           expect(subtraction).toBe(-5);
       });
       
       it('should subtract 6 and 3', function(){
           var subtraction = calculator.subtract(6, 3);
           expect(subtraction).toBe(3);
       });
   });
   
   describe('division', function(){
       it('should divide 18 and 2', function(){
           var division = calculator.divide(18, 2);
           expect(division).toBe(9);
       });
       
       it('should throw an Error if operand B is zero', function(){
           expect(function(){
               calculator.divide(9, 0);
           }).toThrow(new Error('Operand B must be different of zero.'));
       });
   });
});